  const { type, payload } = action;
  console.log(payload)
  switch (type) {
    case ADD_TO_LIST:
      //return { userList: action.payload.userList };
      return produce(state, draft => {
        draft.push(payload);
        return draft;
      })
    case CLEAR_LIST:

      return produce(state, draft => {
        draft = [];
        return draft;
      })

    default:
      return state;
  }
};


