import React from "react";
import Card from "@material-ui/core/Card";
import CardHeader from "@material-ui/core/CardHeader";
import CardContent from "@material-ui/core/CardContent";
import CardActions from "@material-ui/core/CardActions";
import Grid from "@material-ui/core/Grid";
import Typography from "@material-ui/core/Typography";
import Button from "@material-ui/core/Button";
import { Form } from "react-final-form";
import { TextField } from "@material-ui/core";
import composeValidators from "./composeValidators";
import { useDispatch } from "react-redux";
import { addToList, clearList } from "./actions/formActions";
import store from "./store";
function Exercise() {
  const dispatch = useDispatch();
  const handleSubmit = () => {
    const user = {
      name: name,
      email: email,
    };
    dispatch(addToList(user));
    setName("");
    setEmail("");
  };

  // const removeList = (e) => {
  //   e.preventDefault();
  //   const userList = store.getState().form.userList;
  //   dispatch(clearList(userList))
  // }

  const onSubmit = values => {
    //await sleep(300)
    window.alert(JSON.stringify(values, 0, 2))
  }

  const [name, setName] = React.useState("");
  const [email, setEmail] = React.useState("");
  const userList = store.getState().form.userList;
  const required = (value) => (value ? undefined : "Required");
  const mustBeEmail = (value) => (isNaN(value) ? "Invalid Email" : undefined);

  return (
    <Grid container spacing={3}>
      <Grid item xs={6}>
        <Form
          //onSubmit={onSubmit}
          onSubmit={handleSubmit}
          render={({ form, handleSubmit,meta,values }) => {
            return (
              <form onSubmit={handleSubmit} noValidate>
                <Card>
                  <CardHeader title="Form" />
                  <CardContent>test</CardContent>
                  <CardActions>
                    <TextField name="firstName" validate={required}>
                      
                        <div>
                          <label>First Name</label>
                          <input
                            {...input}
                            type="text"
                            placeholder="First Name"
                          />
                          {meta.error && meta.touched && (
                            <span>{meta.error}</span>
                          )}
                        </div>
                      
                    </TextField>
                    <TextField
                      id="outlined-basic"
                      value={name}
                      label="Name"
                      onChange={(e) => setName(e.target.value)}
                      name="name"
                      type="text"
                      variant="outlined"
                      validate={required}
                    />

                    <TextField
                      id="outlined-basic"
                      value={email}
                      label="Email"
                      onChange={(e) => setEmail(e.target.value)}
                      name="email"
                      type="email"
                      required="true"
                      variant="outlined"
                      validate={composeValidators(required)}
                    />
                    <Button variant="contained" type="submit">
                      Submit
                    </Button>
                  </CardActions>
                </Card>
                print
                <pre>{JSON.stringify(values, 0, 2)}</pre>
              </form>
            );
          }}
        />
      </Grid>
      <Grid item xs={6}>
        <Card>
          <CardHeader title="Submitted Form" />

          <CardContent>
            {userList.map((obj, index) => (
              <div key={index}>
                <br />
                <Typography>
                  <strong>Full Name: {obj.name}</strong>
                </Typography>
                <Typography>
                  <strong>Email: {obj.email}</strong>
                </Typography>
              </div>
            ))}
          </CardContent>
        </Card>
      </Grid>
    </Grid>
  );
}

export default Exercise;
