export const formReducers = produce((draft = store.getState().form.userList, action) => {


  const { type, payload } = action;
  switch (type) {
    case ADD_TO_LIST: {
      const { user } = payload;

     let newUser = {
			name: user.name,
			email: user.email
    };
    console.log(newUser)
    draft.push(newUser);
    localStorage.setItem("userList", JSON.stringify(draft));
      console.log(draft)
      return draft;
    }
    case CLEAR_LIST: {
      draft = [];
      localStorage.setItem("userList", JSON.stringify(draft));
      return draft;
    }
    default: {
      return draft;
    }
  }
});
