export const formReducers = (state = { userList: JSON.parse(localStorage.getItem("userList") || "[]")}, action) =>
  produce(state, draft => {
    const { type, payload } = action;
    switch (type) {
      case ADD_TO_LIST: {
        const { user } = payload;
        draft.push({user});
        localStorage.setItem("userList", JSON.stringify(draft));
        return draft;
      }

      case CLEAR_LIST: {
        draft = [];
        localStorage.setItem("userList", JSON.stringify(draft));
        return draft;
      }

      default:
        return draft
    }
  })
