import { ADD_TO_LIST } from "../actions/types";
import store from "../store";

export const addToList = (user) => {
    console.log(user)
  if (user.email !== "") {
    const userList = store.getState().form.userList;

    let alreadyExists = false;
    userList.forEach((el) => {
      if (el.name === user.name && el.email === user.email) {
        alreadyExists = true;
        console.log("FAIL");
      }
    });
    if (!alreadyExists) {
      userList.push({ ...user });

      console.log("SUCCESS");
    }
    localStorage.setItem("userList", JSON.stringify(userList));
    return {
      type: ADD_TO_LIST,
      payload: { userList },
    };
  } else {
    //console.log("Can't be blank");

  }
};

export const removeFromForm = (product) => {
    const cartItems = store.getState().cart.cartItems.slice().filter((item) => item.id !== product.id);
    localStorage.setItem("cartItems", JSON.stringify(cartItems));
    return {
      type: REMOVE_FROM_CART,
      payload: { cartItems }
    };
  };
