import { createStore, applyMiddleware, combineReducers, compose } from "redux";
import { formReducers } from "./reducers/cartReducers";

const store = createStore(
  combineReducers({
    form: formReducers,
  })

  // compose(applyMiddleware(thunk))
);
export default store;
