import React from "react";
import Card from "@material-ui/core/Card";
import CardHeader from "@material-ui/core/CardHeader";
import CardContent from "@material-ui/core/CardContent";
import CardActions from "@material-ui/core/CardActions";
import Grid from "@material-ui/core/Grid";
import Typography from "@material-ui/core/Typography";
import Button from "@material-ui/core/Button";
import { Form,Field } from "react-final-form";
import { TextField } from "@material-ui/core";
import PersonIcon from "@material-ui/icons/Person";
import EmailIcon from "@material-ui/icons/Email";
import composeValidators from "./composeValidators";
import { useDispatch } from "react-redux";
import { addToList, clearList } from "./actions/formActions";
import store from "./store";
import {
  createValidator,
  //composeValidators,
  // combineValidators,
  isRequired,
  //isAlphabetic,
  //isNumeric
} from "revalidate";
function Exercise() {
  const dispatch = useDispatch();
  //const customIsRequired = isRequired({ message: "Required" });
  const isValidEmail = (value) => {
      if (value && !/^[A-Z0-9._%+-]+@[A-Z0-9.-]+\.[A-Z]{2,4}$/i.test(value)) {
        return value;
      }
  }
    
    
  
  const handleSubmit = () => {
    const user = {
      name: name,
      email: email,
    };
    dispatch(addToList(user));
    setName("");
    setEmail("");
  };

  const removeList = (e) => {
    e.preventDefault();
    const userList = store.getState().form.userList;
    dispatch(clearList(userList));
  };

  const [name, setName] = React.useState("");
  const [email, setEmail] = React.useState("");
  //const [error, setError] = React.useState("");
  const userList = store.getState().form.userList;

  return (
    <Grid container spacing={3}>
      <Grid item xs={6}>
        <Form
          onSubmit={handleSubmit}
          render={({ form, handleSubmit, submitting }) => {
            return (
              <form onSubmit={handleSubmit}>
                <Card>
                  <CardHeader title="Form" />
                  <CardContent>test</CardContent>
                  <CardActions>
                    <div>
                      <PersonIcon />
                      <TextField
                        value={name}
                        onChange={(e) => setName(e.target.value)}
                        name="name"
                        type="text"
                        id="outlined-error-helper-text"
                        label="Name"
                        placeholder="..."
                        variant="outlined"
                      />
                      <EmailIcon />
                       <TextField
                        value={email}
                        onChange={(e) => setEmail(e.target.value)}
                        id="outlined-error-helper-text"
                        label="Email"
                        placeholder="Required"
                        variant="outlined"
                        required
                        validate={composeValidators(isValidEmail)}
                      /> 
                    
                      <TextField
                        name="email"
                        validate={composeValidators(isValidEmail)}
                      >
                        {({  meta }) => (
                          <div>
                            <input
                             value={email}
                              onChange={(e) => setEmail(e.target.value)}
                              type="email"
                              placeholder="Email"
                            />
                            {meta.error && (
                              <span>{meta.error}</span>
                            )}
                          </div>
                        )}
                      </TextField>
                    </div>
                    <Button
                      disabled={submitting}
                      variant="contained"
                      type="submit"
                    >
                      Submit
                    </Button>
                  </CardActions>
                </Card>
              </form>
            );
          }}
        />
      </Grid>
      <Grid item xs={6}>
        <Card>
          <CardHeader title="Submitted Form" />
          <Button variant="contained" onClick={removeList}>
            Remove List
          </Button>
          <CardContent>
            {userList.map((obj, index) => (
              <div key={index}>
                <br />
                <Typography>
                  <strong>Full Name: {obj.name}</strong>
                </Typography>
                <Typography>
                  <strong>Email: {obj.email}</strong>
                </Typography>
              </div>
            ))}
          </CardContent>
        </Card>
      </Grid>
    </Grid>
  );
}

export default Exercise;
