import { ADD_TO_LIST, CLEAR_LIST } from "../actions/types";
//import produce from "immer";
const initialState = { userList: JSON.parse(localStorage.getItem("userList") || "[]" )}
export const formReducers = ( state = initialState, action ) => {
  console.log(action.payload)
  switch (action.type) {
    case ADD_TO_LIST:
      return { userList: action.payload.userList };
    case CLEAR_LIST:
      if (initialState.length != initialState.length){
      return { userList: action.payload.userList };
      }
    default:
      return state;
  }
};

