import { ADD_TO_FORM } from "../actions/types";
import store from "../store";

export const addToForm = (user) => {
  //const cartItems = store.getState().cart.cartItems.slice();
  console.log(user);
  console.log(store.getState());
  const userList = store.getState().form.userList;

  let alreadyExists = false;
  userList.forEach((el) => {
    if (el.name === user.name && el.email === user.email) {
      alreadyExists = true;
      console.log("FAIL");
    }
  });
  if (!alreadyExists) {
    userList.push({ ...user });

    console.log("SUCCESS");
  }

  localStorage.setItem("userList", JSON.stringify(userList));
  return {
    type: ADD_TO_FORM,
    payload: { userList },
  };
};
