export const formReducers = produce((state = { draft: JSON.parse(localStorage.getItem("userList") || "[]") }, action) => {

  switch (action.type) {
    case ADD_TO_LIST: {
      const { user } = action.payload;

      console.log(userList)
      userList.push(user);

      return userList;
    }
    case CLEAR_LIST: {

      return draft;
    }
    default: {
      return draft;
    }
  }
});
