import React from "react";
import Card from "@material-ui/core/Card";
import CardHeader from "@material-ui/core/CardHeader";
import CardContent from "@material-ui/core/CardContent";
import CardActions from "@material-ui/core/CardActions";
import Grid from "@material-ui/core/Grid";
import Typography from "@material-ui/core/Typography";
import Button from "@material-ui/core/Button";
import { Form } from "react-final-form";
import { TextField } from "@material-ui/core";
//import composeValidators from "./composeValidators";
import { useDispatch, useSelector } from "react-redux";
import { addToForm } from "./actions/formActions";
import store from "../store";
function Exercise() {
  
  const dispatch = useDispatch();
  const handleSubmit = () => {
    const user = {
      name: name,
      email: email,
    };
    dispatch(addToForm(user));
    setName("");
    setEmail("");
  };

  const [name, setName] = React.useState("");
  const [email, setEmail] = React.useState("");
  let [list, setList] =  store.getState().form.userList;;
  console.log(name);
  console.log(email);
  return (
    <Grid container spacing={3}>
      <Grid item xs={6}>
        <Form
          onSubmit={handleSubmit}
          render={({ form, handleSubmit }) => {
            return (
              <form onSubmit={handleSubmit} noValidate>
                <Card>
                  <CardHeader title="Form" />
                  <CardContent></CardContent>
                  <CardActions>
                    <TextField
                      id="outlined-basic"
                      value={name}
                      label="Name"
                      onChange={(e) => setName(e.target.value)}
                      name="name"
                      type="text"
                      variant="outlined"
                    />

                    <TextField
                      id="outlined-basic"
                      value={email}
                      label="Email"
                      onChange={(e) => setEmail(e.target.value)}
                      name="email"
                      type="email"
                      required
                      variant="outlined"
                    />
                    <Button fullWidth variant="contained" type="submit">
                      Submit
                    </Button>
                  </CardActions>
                </Card>
              </form>
            );
          }}
        />
      </Grid>
      <Grid item xs={6}>
        <Card>
          <CardHeader title="Submitted Form" />
          <CardContent>
            {setList.map((obj,index) => (

              <div key={index}>
                {obj.name}
                {obj.email}
              </div>
            ))}
            <Typography>
              <strong>Full Name: </strong>
            </Typography>
            <Typography>
              <strong>Email: </strong>
            </Typography>
          </CardContent>
        </Card>
      </Grid>
    </Grid>
  );
}

export default Exercise;
