export const formReducers = (
  state = { userList: JSON.parse(localStorage.getItem("userList") || "[]") },
  action
) =>
  produce(state, (draft) => {
    const { type, payload } = action;
    switch (type) {
      case ADD_TO_LIST: {
        const { user } = payload;

          draft.userList.push(user);
          localStorage.setItem("userList", JSON.stringify(draft.userList));

      return draft;
    }

      case CLEAR_LIST: {

        localStorage.setItem("userList", JSON.stringify(payload));
        return draft;
      }
      default:
        return draft;
    }
  });
