import React from "react";
import Card from "@material-ui/core/Card";
import CardHeader from "@material-ui/core/CardHeader";
import CardContent from "@material-ui/core/CardContent";
import CardActions from "@material-ui/core/CardActions";
import Grid from "@material-ui/core/Grid";
import Typography from "@material-ui/core/Typography";
import Button from "@material-ui/core/Button";
import { Form } from "react-final-form";
import { TextField } from "@material-ui/core";
import composeValidators from "./composeValidators";
import { useDispatch } from "react-redux";
import { addToList, clearList } from "./actions/formActions";
import store from "./store";
function Exercise() {
  const dispatch = useDispatch();

  const handleSubmit = () => {
    const user = {
      name: name,
      email: email,
    };
    dispatch(addToList(user));
    setName("");
    setEmail("");
  };

  const removeList = () => {
    //e.preventDefault();
    const userList = store.getState().form.userList;
    dispatch(clearList(userList))
  }

  const [name, setName] = React.useState("");
  const [email, setEmail] = React.useState("");
  const [error, setError] = React.useState("");
  const userList = store.getState().form.userList;
  const mustBeNumber = value => {
    isNaN(value) ? setError("Must be a number") : setError("");
    //console.log(email);
  };
  //const isValid = "/^[A-Z0-9._%+-]+@[A-Z0-9.-]+\.[A-Z]{2,}$/i"
  //const mustBeEmail = (value) => (isNaN(value) ? "Invalid Email" : undefined);
  //const mustBeNumber = value => (isNaN(value) ? 'Must be a number' : '')

  return (
    <Grid container spacing={3}>
      <Grid item xs={6}>
        <Form
          //onSubmit={onSubmit}
          onSubmit={handleSubmit}
          render={({ form, handleSubmit,removeList }) => {
            return (
              <form onSubmit={handleSubmit}>
                <Card>
                  <CardHeader title="Form" />
                  <CardContent>test</CardContent>
                  <CardActions>
                    <div>
                      <TextField
                        value={name}
                        onChange={(e) => setName(e.target.value)}
                        name="name"
                        type="text"
                        id="outlined-error-helper-text"
                        label="Name"
                        placeholder="..."
                        multiline
                        variant="outlined"
                      />

                      <TextField
                        value={email}
                        onChange={(e) => setEmail(e.target.value)}
                        id="outlined-error-helper-text"
                        label="Email"
                        placeholder="Required"
                        multiline
                        variant="outlined"
                        required
                        validate={composeValidators(mustBeNumber)}
                        //helperText={error}
                      />
                      {error}
                    </div>
                    <Button variant="contained" type="submit">
                      Submit
                    </Button>
                    <Button variant="contained" onClick={removeList} >
                      Remove List
                    </Button>
                  </CardActions>
                </Card>
              </form>
            );
          }}
        />
      </Grid>
      <Grid item xs={6}>
        <Card>
          <CardHeader title="Submitted Form" />

          <CardContent>
            {userList.map((obj, index) => (
              <div key={index}>
                <br />
                <Typography>
                  <strong>Full Name: {obj.name}</strong>
                </Typography>
                <Typography>
                  <strong>Email: {obj.email}</strong>
                </Typography>
              </div>
            ))}
          </CardContent>
        </Card>
      </Grid>
    </Grid>
  );
}

export default Exercise;
