export const formReducers = produce((draft = { userList: JSON.parse(localStorage.getItem("userList") || "[]") }, action) => {
  const { type, payload } = action;
  switch (type) {
    case ADD_TO_LIST: {
      const { user } = payload;

      draft.push({"name": payload.name, "age": payload.email});
      return draft;
    }
    case CLEAR_LIST: {
      draft = [];
      localStorage.setItem("userList", JSON.stringify(draft));
      return draft;
    }
    default: {
      return draft;
    }
  }
});
