export const formReducers = produce((draft, action) => {
  switch (action.type) {
    case ADD_TO_LIST: {
      const { value } = action.payload;
      draft.push(value);
      return draft;
    }
    case CLEAR_LIST: {

      return draft;
    }
    default: {
      return draft;
    }
  }
});
