//     default:
//       return state;
//   }
// };


export const formReducers = produce((draft = { userList: JSON.parse(localStorage.getItem("userList") || "[]") }, action) => {
  const { type, payload } = action;
  switch (type) {
    case ADD_TO_LIST: {
      const { value } = payload;
      draft.push(value);
      return draft;
    }
    case CLEAR_LIST: {
      const { index } = payload;
      draft = [];
      return draft;
    }
    case 'update': {
      const { index, value } = payload;
      draft[index] = value;
      return draft;
    }
    default: {
      return draft;
    }
  }
});
