import { ADD_TO_LIST, CLEAR_LIST } from "../actions/types";

export const formReducers = ( state = {cartItems: JSON.parse(localStorage.getItem("cartItems") || "[]") },action) => {
  console.log(action.payload)
  switch (action.type) {
    case ADD_TO_LIST:
      return { userList: action.payload.userList };
    default:
      return state;
  }
};
