export const formReducers = (
  state = { userList: JSON.parse(localStorage.getItem("userList") || "[]") }, action) =>
  produce(state, (draft) => {
    switch (action.type) {
      case ADD_TO_LIST: {
        draft[action.payload.userList] = action.payload;
        // break
      }
      case CLEAR_LIST:
        return { userList: action.payload.userList };
      default:
        return state;
    }
  });

