export const formReducers = (state = { userList: JSON.parse(localStorage.getItem("userList") || "[]")}, action) =>
  produce(state, draft => {
    switch (action.type) {
      case ADD_TO_LIST: {
        const { user } = action.payload;
        console.log(user.name)
        console.log(user.email)
        draft.push({"name": user.name, "email": user.email});
        localStorage.setItem("userList", JSON.stringify(draft));
        return draft;

      }

      case CLEAR_LIST: {
        draft = [];
        localStorage.setItem("userList", JSON.stringify(draft));
        return draft;

      }

      default:
        return draft
    }
  })
