export const formReducers = ( state = [], action ) => {
  console.log(action.payload)
  const { type, payload } = action;
  switch (type) {
    case ADD_TO_LIST:
      //return { userList: action.payload.userList };
      return produce(state, draft => {
        draft.push(payload);
        return draft;
      })
    case CLEAR_LIST:

      return produce(state, draft => {
        draft.delete(payload);
        return draft;
      })

    default:
      return state;
  }
};


