import { ADD_TO_LIST, CLEAR_LIST } from "../actions/types";
import store from "../store";

export const addToList = (user) => {
  const userList = store.getState().form.userList;
  let alreadyExists = false;
  userList.forEach((el) => {
    if (el.email === user.email) {
      alreadyExists = true;
      window.alert("This email already taken, please change");
    }
  });
  if (!alreadyExists) {
    userList.push({ ...user });
    console.log(user);
    localStorage.setItem("userList", JSON.stringify(userList));
  }
  return {
    type: ADD_TO_LIST,
    payload: { userList },
  };
};

export const clearList = (userList) => {
  userList = [];
  localStorage.setItem("userList", JSON.stringify(userList));
  return {
    type: CLEAR_LIST,
    payload: { userList },
  };
};
