export const formReducers = produce((draft, action) => {

  const { type, payload } = action;
  switch (type) {
    case ADD_TO_LIST: {
      const { value } = payload;
      console.log(value)
      console.log(draft)
      draft.push(value);

      return draft;
    }
    case CLEAR_LIST: {

      return draft;
    }
    default: {
      return draft;
    }
  }
});
